/**
 * Get exercises by category
 */
export function getExercisesByCategory(category) {
    return EXERCISE_DATABASE.filter(ex => ex.category === category);
}

/**
 * Get exercises by equipment
 */
export function getExercisesByEquipment(equipment) {
    return EXERCISE_DATABASE.filter(ex => ex.equipment === equipment);
}

/**
 * Get exercises by difficulty
 */
export function getExercisesByDifficulty(difficulty) {
    return EXERCISE_DATABASE.filter(ex => ex.difficulty === difficulty);
}

/**
 * Search exercises by name
 */
export function searchExercises(query) {
    const lowerQuery = query.toLowerCase();
    return EXERCISE_DATABASE.filter(ex =>
        ex.name.toLowerCase().includes(lowerQuery) ||
        ex.muscleGroup.toLowerCase().includes(lowerQuery)
    );
}

